package com.aash.vetsystem.view

import android.content.Intent
import android.graphics.Bitmap
import android.os.Bundle
import android.os.Handler
import androidx.appcompat.app.AppCompatActivity
import androidx.databinding.DataBindingUtil
import com.aash.vetsystem.Preferences.VetPreferences
import com.aash.vetsystem.R
import com.aash.vetsystem.utils.HelperClass
import com.aash.vetsystem.databinding.ActivityMainBinding

class SplashScreen : AppCompatActivity() {
    lateinit var bindingAc: ActivityMainBinding
    lateinit var company: String
    lateinit var description: String
    lateinit var logoBase64: String
    lateinit var bitmap: Bitmap
    lateinit var pref: VetPreferences

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        bindingAc = DataBindingUtil.setContentView(this,R.layout.activity_main);
        pref = VetPreferences(applicationContext)
        company = pref.company.toString()
        description = pref.description.toString()
        logoBase64 = pref.image.toString()
        if (logoBase64.equals("")) {
            logoBase64 =
                ""
            company = getString(R.string.your_name)
            description = getString(R.string.description)
        }
        val helperClass = HelperClass()
        bitmap = helperClass.base64ToBitmap(logoBase64)
        bindingAc.companyName = company
        bindingAc.description = description
        bindingAc.logo = bitmap
        Handler().postDelayed({
            startActivity(Intent(this, Login::class.java))
            finish()
        }, 2000)

    }
}